.protyle-wysiwyg .code-block {
    --b3-protyle-code-linenumber-hl: color-mix(in srgb, var(--b3-theme-surface) 55%, var(--b3-theme-primary));
    margin: .4em 0 1em 0 !important; /* bottom 连续代码块给命名腾出个地方 */
    padding: 0.3em 0.2em 0.3em 0.2em !important;
}

/* 代码块整体 默认设置存在行号*/
.protyle-wysiwyg .code-block .hljs {
    padding: 0.3em 0.2em 0.3em 0.2em;
    background-color: var(--b3-code-block-backgroud-color);
    /* margin-left: 2.2em; 没有行号的时候清空样式 */
    position: inherit;
    max-height: 550px; /* 设置单一代码块最大高度 */
}

/* 隐藏代码块后 去除上面的 padding 和 maring 显示第一行代码 */
.protyle-wysiwyg .code-block[fold="1"] .hljs {
    padding-top: 0;
    margin-top: 0;
}

/* 行号 */
.protyle-wysiwyg .code-block .protyle-linenumber__rows {
    padding-top: 0px;
    font-size: 12px;
    width: 2.2em;
}

/* 标题栏 */
.protyle-wysiwyg[data-readonly=false] .code-block .protyle-action {
    -webkit-user-modify: read-only;
    /* 修复第一行代码不可点击 修复后 -> 前85%的位置可以点击 */
    position: absolute;
    left: 95%;
    top: 0;
}

/* 语言选择栏 */
.code-block .protyle-action .protyle-action__language {
    -webkit-user-modify: read-only;
    margin-top: 0;
    position: absolute;
    right: inherit;
    float: right;
    width: 90px;
    padding-left: 0;
    margin-left: 0;
    margin-right: 4em;
    text-align: right;
}

/* 复制按钮 */
.protyle-action .protyle-action__copy {
    border-top-left-radius: 8px;
    border-bottom-left-radius: 8px;
    /*border-radius: 8px;*/
    padding-left: 6px;
}

/* 复制按钮 折叠按钮 */
.protyle-action .protyle-icon--expand {
    border-top-right-radius: 8px;
    border-bottom-right-radius: 8px;
}

/* 更多按钮 */
