// version 0.0.5
// 0.0.1 æ”¯æŒä»£ç å—çš„æŠ˜å å’Œå±•å¼€ï¼Œå…¨å±å’Œæ‚¬æµ®æ¨ªå‘æ»šåŠ¨æ¡
// 0.0.2 ç¾åŒ–æ»šåŠ¨æ¡æ ·å¼
// 0.0.3 ä¿®å¤å…¨å±åä»£ç å—æ˜¾ç¤ºä¸å…¨é—®é¢˜
// 0.0.4 æ”¹è¿›ä»…ä»£ç å—æœ‰æ»šåŠ¨æ¡ä¸”å†…å®¹é«˜åº¦å¤§äºcodeMaxHeightæ—¶æ‰æ˜¾ç¤º
// 0.0.5 ä¿®å¤æ–°å»ºä»£ç å—ä¸ä¼šç«‹åˆ»å‡ºç°æŠ˜å æ‰©å±•ç®­å¤´
(() => {
  // å½“ä»£ç å—å†…å®¹æœ€å¤§é«˜åº¦ï¼Œæ³¨æ„ï¼šè¿™é‡Œçš„é«˜åº¦æ˜¯æŒ‡.hljså…ƒç´ çš„é«˜åº¦ï¼Œé»˜è®¤æ˜¯500px
  // æ”¯æŒåœ¨å—ä¸Šæ·»åŠ auto-heightè‡ªå®šä¹‰å±æ€§ï¼Œä½¿å—ä¸å—æœ€å¤§é«˜åº¦é™åˆ¶
  const codeMaxHeight = '500px';

  // æ˜¯å¦æ˜¾ç¤ºå…¨å±æŒ‰é’® true æ˜¾ç¤º false ä¸æ˜¾ç¤º
  const isEnableFullscreen = false;

  // æ˜¯å¦æ˜¾ç¤ºæ¨¡æ‹Ÿæ»šåŠ¨æ¡ true æ˜¾ç¤º false ä¸æ˜¾ç¤º
  // è¯¥åŠŸèƒ½åœ¨ä»£ç å—åº•éƒ¨è¶…å‡ºå¯è§†åŒºåŸŸæ—¶è‡ªåŠ¨åœ¨åº•éƒ¨æ˜¾ç¤ºæ»šåŠ¨æ¡
  const isEnableScrollbar = false;

  // ä¸æ”¯æŒæ‰‹æœºç‰ˆï¼ˆå› ä¸ºæ‰‹æœºç‰ˆä¸éœ€è¦ï¼‰
  if (isMobile()) return;

  // æ·»åŠ æ ·å¼
  addStyle(`
        .b3-typography div.hljs, .protyle-wysiwyg .code-block:not([custom-auto-height]) div.hljs {
            max-height: ${codeMaxHeight || '500px'};
        }
        .b3-typography .code-block:not(pre), .protyle-wysiwyg .code-block:not(pre){
            margin: 2px 0; padding: 4px;
        }
        .b3-typography div.hljs, .protyle-wysiwyg div.hljs{
                padding: 0.65em 1em 1.6em;
        }
        .b3-typography div.protyle-action, .protyle-wysiwyg .code-block div.protyle-action {
            position: sticky;
        }
        /* å…¨å±èƒŒæ™¯è‰² */
        :not(:root):fullscreen::backdrop {
            background-color: var(--b3-theme-background);
        }
        /* æ¨¡æ‹Ÿæ»šåŠ¨æ¡å®¹å™¨ */
        .scrollbar-container {
          position: sticky;
          bottom: 0;
          width: 100%;
          height: 8px;
          /*background-color: #ddd;*/
          cursor: pointer;
          border-radius: 5px;
          /*transition: opacity 0.3s ease;*/
          z-index: ${++siyuan.zIndex || 9999};
        }
        /* æ»šåŠ¨æ¡æ»‘å— */
        .scrollbar-thumb {
          position: absolute;
          top: 0;
          left: 0;
          height: 100%;
          width: 20%; /* åˆå§‹æ»‘å—å®½åº¦ */
          /*background-color: var(--b3-scroll-color);*/ /* light #C2C3C3 dark #4E4F58 */
          box-shadow: inset 0 0 5px 5px var(--b3-scroll-color);
          border-radius: 5px;
          cursor: grab;
        }
        .scrollbar-container.f__hidden {
          opacity: 0; /* éšè—å…ƒç´  */
          pointer-events: none; /* ç¦ç”¨é¼ æ ‡äº¤äº’ */
          height: 0;
        }
    `);

  // ç›‘å¬ä»£ç å—è¢«åŠ è½½
  whenElementExist('.layout__center, #editor').then(async el => {
    // åŠ è½½æ—¶æ‰§è¡Œï¼ˆé™æ€åŠ è½½ï¼‰
    let protyle;
    await whenElementExist(() => {
      protyle = el.querySelector('.protyle');
      return protyle && protyle?.dataset?.loading === 'finished';
    });
    addCodeExtends(protyle.querySelectorAll('.code-block:not(:has(.protyle-icon--expand))'), protyle);

    // æ»šåŠ¨æ—¶æ‰§è¡Œ
    protyle.querySelector(".protyle-content").addEventListener('scroll', () => {
      addCodeExtends(protyle.querySelectorAll('.code-block:not(:has(.protyle-icon--expand))'), protyle);
    });

    // ç›‘å¬protyleåŠ è½½äº‹ä»¶ï¼ˆåŠ¨æ€åŠ è½½ï¼‰
    observeProtyleLoaded(el, protyles => {
      protyles.forEach(async protyle => {
        if (!protyle.classList.contains('protyle')) {
          protyle = protyle.closest('.protyle');
        }
        // åŠ è½½æ—¶æ‰§è¡Œ
        addCodeExtends(protyle.querySelectorAll('.code-block:not(:has(.protyle-icon--expand))'), protyle);

        // æ»šåŠ¨æ—¶æ‰§è¡Œ
        protyle.querySelector(".protyle-content").addEventListener('scroll', () => {
          addCodeExtends(protyle.querySelectorAll('.code-block:not(:has(.protyle-icon--expand))'), protyle);
        });
      });
    });
  });

  // æ·»åŠ æ‰©å±•æŒ‰é’®
  let runing = false;
  function addCodeExtends(codeBlocks, protyle) {
    if (codeBlocks.length === 0) return;
    if (runing) return;
    runing = true;
    setTimeout(() => { runing = false; }, 500);
    codeBlocks.forEach(async code => {
      if (code.querySelector('.protyle-icon--expand')) return;

      // æ·»åŠ æŠ˜å æŒ‰é’®
      const hljs = code.querySelector('.hljs');
      if (!hljs) return;
      let expandStatus = getExpandStatus(hljs);
      const ariaLabel = getAriaLabelText(expandStatus);
      const expandBtnHtml = `<span class="b3-tooltips__nw b3-tooltips protyle-icon protyle-icon--expand protyle-action__expand protyle-custom" aria-label="${ariaLabel}"><svg><use xlink:href="#${expandStatus}"></use></svg></span>`;
      const moreBtn = code.querySelector('.protyle-icon--last');
      if (!moreBtn) return;
      await whenElementExist(() => moreBtn.getAttribute('aria-label'));
      moreBtn.insertAdjacentHTML('beforebegin', expandBtnHtml);
      const expandBtn = code.querySelector('.protyle-icon--expand');
      expandBtn.addEventListener('click', () => {
        expandStatus = getExpandStatus(hljs);
        if (expandStatus === 'iconDown') {
          hljs.style.maxHeight = 'none';
          expandStatus = 'iconUp';
        } else {
          hljs.style.maxHeight = codeMaxHeight;
          expandStatus = 'iconDown';
        }
        const useEl = expandBtn.querySelector('svg > use');
        useEl.setAttributeNS('http://www.w3.org/1999/xlink', 'xlink:href', '#' + expandStatus);
        const ariaLabel = getAriaLabelText(expandStatus);
        expandBtn.setAttribute('aria-label', ariaLabel);
      });

      // æ·»åŠ å…¨å±æŒ‰é’®
      if (!isEnableFullscreen) return;
      if (code.querySelector('.protyle-icon--fullscreen')) return;
      let fullscreenAriaLabel = 'å…¨å±';
      let fullscreenStatus = 'iconFullscreen';
      const fullscreenBtnHtml = `<span class="b3-tooltips__nw b3-tooltips protyle-icon protyle-icon--fullscreen protyle-action__fullscreen protyle-custom" aria-label="${fullscreenAriaLabel}"><svg><use xlink:href="#${fullscreenStatus}"></use></svg></span>`;
      expandBtn.insertAdjacentHTML('beforebegin', fullscreenBtnHtml);
      const fullscreenBtn = code.querySelector('.protyle-icon--fullscreen');
      let oldCodeMaxHeight;
      fullscreenBtn.addEventListener('click', () => {
        if (fullscreenStatus === 'iconFullscreen') {
          oldCodeMaxHeight = hljs.style.maxHeight;
          requestFullScreen(code);
          fullscreenStatus = 'iconFullscreenExit';
          fullscreenAriaLabel = 'é€€å‡ºå…¨å±';
          hljs.style.maxHeight = 'calc(100vh - 58px)';
          expandBtn.style.display = 'none';
          if (scrollbarContainer) scrollbarContainer.classList.add('f__hidden');
        } else {
          exitFullScreen(code);
          fullscreenStatus = 'iconFullscreen';
          fullscreenAriaLabel = 'å…¨å±';
          if (oldCodeMaxHeight !== undefined) hljs.style.maxHeight = oldCodeMaxHeight;
          expandBtn.style.display = '';
          setTimeout(() => {
            if (scrollbarContainer) scrollbarContainer.classList.add('f__hidden');
          }, 300);
        }
        const useEl = fullscreenBtn.querySelector('svg > use');
        useEl.setAttributeNS('http://www.w3.org/1999/xlink', 'xlink:href', '#' + fullscreenStatus);
        fullscreenBtn.setAttribute('aria-label', fullscreenAriaLabel);
      });

      // æ·»åŠ æ¨¡æ‹Ÿæ»šåŠ¨æ¡
      if (!isEnableScrollbar) return;
      if (code.querySelector('.scrollbar-container')) return;
      const scrollbarHtml = `<div class="scrollbar-container protyle-custom"><div class="scrollbar-thumb"></div></div>`;
      code.insertAdjacentHTML('beforeend', scrollbarHtml);
      const scrollbarContainer = code.querySelector('.scrollbar-container');
      const protyleContent = protyle.querySelector(".protyle-content");

      // æ£€æŸ¥æ˜¯å¦éœ€è¦æ˜¾ç¤ºæ¨¡æ‹Ÿæ»šåŠ¨æ¡
      function checkScrollbarVisibility() {
        const hasHorizontalScrollbar = hljs.scrollWidth > hljs.clientWidth; // æ˜¯å¦æœ‰æ¨ªå‘æ»šåŠ¨æ¡
        const isHeightExceeded = hljs.scrollHeight > parseHeightToPixels(codeMaxHeight); // é«˜åº¦æ˜¯å¦è¶…è¿‡codeMaxHeight
        const isSticky = !isElementBottomInViewport(code); // æ˜¯å¦å¤„äº sticky çŠ¶æ€

        if (hasHorizontalScrollbar && isHeightExceeded && isSticky) {
          scrollbarContainer.classList.remove('f__hidden'); // æ˜¾ç¤ºæ¨¡æ‹Ÿæ»šåŠ¨æ¡
        } else {
          scrollbarContainer.classList.add('f__hidden'); // éšè—æ¨¡æ‹Ÿæ»šåŠ¨æ¡
        }
      }

      // åˆå§‹åŒ–æ»šåŠ¨æ¡çŠ¶æ€
      checkScrollbarVisibility();

      // ç›‘å¬ protyleContent çš„æ»šåŠ¨äº‹ä»¶
      protyleContent.addEventListener('scroll', () => {
        checkScrollbarVisibility();
      });

      // æ¨¡æ‹Ÿæ»šåŠ¨æ¡æ»šåŠ¨
      const scrollbarThumb = code.querySelector(".scrollbar-thumb");
      let isDragging = false; // æ˜¯å¦æ­£åœ¨æ‹–åŠ¨
      let startX, thumbStartX; // é¼ æ ‡æŒ‰ä¸‹æ—¶çš„åˆå§‹ä½ç½®

      // è®¡ç®—æ»‘å—å®½åº¦å’Œæ»šåŠ¨æ¯”ä¾‹
      function updateScrollbar() {
        const contentWidth = hljs.scrollWidth;
        const viewportWidth = hljs.clientWidth;
        let thumbWidth = (viewportWidth / contentWidth) * scrollbarContainer.offsetWidth;
        // è¾¹ç•Œå€¼å¤„ç†
        thumbWidth = Math.max(thumbWidth, 10); // æœ€å°å®½åº¦ä¸º10px
        scrollbarThumb.style.width = `${thumbWidth}px`;
      }

      // åŒæ­¥æ»šåŠ¨æ¡ä½ç½®
      function syncScrollbarPosition() {
        const scrollPercentage = hljs.scrollLeft / (hljs.scrollWidth - hljs.clientWidth);
        const thumbMaxMove = scrollbarContainer.offsetWidth - scrollbarThumb.offsetWidth;
        scrollbarThumb.style.left = `${scrollPercentage * thumbMaxMove}px`;
      }

      // åˆå§‹åŒ–æ»šåŠ¨æ¡
      updateScrollbar();
      syncScrollbarPosition();

      // ç›‘å¬ .code-block çš„æ»šåŠ¨äº‹ä»¶
      hljs.addEventListener("scroll", () => {
        syncScrollbarPosition();
      });

      // æ¨¡æ‹Ÿæ»šåŠ¨æ¡æ‹–åŠ¨é€»è¾‘
      scrollbarThumb.addEventListener("mousedown", (e) => {
        isDragging = true;
        startX = e.clientX;
        thumbStartX = parseFloat(scrollbarThumb.style.left) || 0;
        // ç¦ç”¨æ–‡æœ¬é€‰æ‹©
        hljs.style.userSelect = "none";
        // ç»‘å®šå…¨å±€äº‹ä»¶
        document.addEventListener("mousemove", handleMouseMove);
        document.addEventListener("mouseup", handleMouseUp);
        // é˜»æ­¢é»˜è®¤è¡Œä¸º
        e.preventDefault();
      });

      function handleMouseMove(e) {
        if (!isDragging) return;
        const deltaX = e.clientX - startX;
        const thumbMaxMove = scrollbarContainer.offsetWidth - scrollbarThumb.offsetWidth;
        let newThumbPosition = thumbStartX + deltaX;
        // é™åˆ¶æ»‘å—èŒƒå›´
        newThumbPosition = Math.max(0, Math.min(newThumbPosition, thumbMaxMove));
        scrollbarThumb.style.left = `${newThumbPosition}px`;
        // åŒæ­¥ .code-block çš„æ»šåŠ¨ä½ç½®
        const scrollPercentage = newThumbPosition / thumbMaxMove;
        hljs.scrollLeft = scrollPercentage * (hljs.scrollWidth - hljs.clientWidth);
        // é˜»æ­¢é»˜è®¤è¡Œä¸º
        e.preventDefault();
      }

      function handleMouseUp() {
        isDragging = false;
        // æ¢å¤æ–‡æœ¬é€‰æ‹©
        hljs.style.userSelect = "";
        // ç§»é™¤å…¨å±€äº‹ä»¶
        document.removeEventListener("mousemove", handleMouseMove);
        document.removeEventListener("mouseup", handleMouseUp);
      }

      // ç›‘å¬çª—å£å¤§å°å˜åŒ–
      window.addEventListener("resize", () => {
        checkScrollbarVisibility();
        updateScrollbar();
        syncScrollbarPosition();
      });
    });
  }

  function requestFullScreen(element) {
    if (element.requestFullscreen) {
      element.requestFullscreen();
    } else if (element.mozRequestFullScreen) { // Firefox
      element.mozRequestFullScreen();
    } else if (element.webkitRequestFullscreen) { // Chrome, Safari, Opera
      element.webkitRequestFullscreen();
    } else if (element.msRequestFullscreen) { // IE/Edge
      element.msRequestFullscreen();
    }
  }

  function exitFullScreen() {
    if (document.exitFullscreen) {
      document.exitFullscreen();
    } else if (document.mozCancelFullScreen) { // Firefox
      document.mozCancelFullScreen();
    } else if (document.webkitExitFullscreen) { // Chrome, Safari, Opera
      document.webkitExitFullscreen();
    } else if (document.msExitFullscreen) { // IE/Edge
      document.msExitFullscreen();
    }
  }

  function getAriaLabelText(expandStatus) {
    return expandStatus === 'iconDown' ? 'å±•å¼€' : 'æŠ˜å ';
  }

  function getExpandStatus(hljs) {
    return getComputedStyle(hljs, null)?.maxHeight === codeMaxHeight ? 'iconDown' : 'iconUp';
  }

  function addStyle(css) {
    // åˆ›å»ºä¸€ä¸ªæ–°çš„styleå…ƒç´ 
    const style = document.createElement('style');
    // è®¾ç½®CSSè§„åˆ™
    style.innerHTML = css;
    // å°†styleå…ƒç´ æ·»åŠ åˆ°<head>ä¸­
    document.head.appendChild(style);
  }

  function observeProtyleLoaded(el, callback) {
    const config = { attributes: false, childList: true, subtree: true };
    const observer = new MutationObserver((mutationsList, observer) => {
      mutationsList.forEach(mutation => {
        if (mutation.type === 'childList') {
          // æŸ¥æ‰¾æ–°å¢åŠ çš„ NodeCodeBlock å…ƒç´ 
          const protyles = Array.from(mutation.addedNodes).filter(node =>
            node.nodeType === Node.ELEMENT_NODE &&
            (node.classList.contains('protyle') || node.classList.contains('protyle-content'))
          );
          // å¦‚æœæ‰¾åˆ°äº†è¿™æ ·çš„å…ƒç´ ï¼Œåˆ™è°ƒç”¨å›è°ƒå‡½æ•°
          if (protyles.length > 0) {
            callback(protyles);
          }

          // ç›‘å¬ä»£ç å—nodeè¢«åˆ›å»º
          const codeBlocks = Array.from(mutation.addedNodes).filter(node =>
            node.nodeType === Node.ELEMENT_NODE &&
            (node.matches('.code-block:not(:has(.protyle-icon--expand))'))
          );
          if (codeBlocks.length > 0) {
            addCodeExtends(codeBlocks, codeBlocks[0].closest('.protyle'));
          }
        }
      });
    });
    // å¼€å§‹è§‚å¯Ÿ body ä¸‹çš„æ‰€æœ‰å˜åŒ–
    observer.observe(el, config);
    // å½“ä¸å†éœ€è¦è§‚å¯Ÿæ—¶å¯è°ƒç”¨æ–­å¼€æ¥åœæ­¢è§‚å¯Ÿ
    return () => {
      observer.disconnect();
    };
  }

  // ç­‰å¾…å…ƒç´ æ¸²æŸ“å®Œæˆåæ‰§è¡Œ
  function whenElementExist(selector, node) {
    return new Promise(resolve => {
      const check = () => {
        const el = typeof selector === 'function' ? selector() : (node || document).querySelector(selector);
        if (el) resolve(el); else requestAnimationFrame(check);
      };
      check();
    });
  }

  function isMobile() {
    return !!document.getElementById("sidebar");
  }

  function isElementBottomInViewport(el) {
    if (!el) return false; // å¦‚æœå…ƒç´ ä¸å­˜åœ¨ï¼Œç›´æ¥è¿”å› false
    const rect = el.getBoundingClientRect(); // è·å–å…ƒç´ çš„è¾¹ç•Œä¿¡æ¯
    const windowHeight = window.innerHeight || document.documentElement.clientHeight;
    // åˆ¤æ–­å…ƒç´ çš„åº•éƒ¨æ˜¯å¦åœ¨è§†å£å†…
    return rect.bottom <= windowHeight;
  }

  // é€šè¿‡å­—ç¬¦ä¸²è§£æå®é™…é«˜åº¦ï¼Œç¼“å­˜å¯¹è±¡ (ä½œä¸ºå‡½æ•°å±æ€§)
  function parseHeightToPixels(value) {
    // é™æ€ç¼“å­˜å¯¹è±¡ (å‡½æ•°å±æ€§)
    if (!parseHeightToPixels.memo) {
      parseHeightToPixels.memo = {};
    }

    // 1. æ•°å­—ç±»å‹ç›´æ¥è¿”å›
    if (typeof value === 'number') {
      return value;
    }

    // 2. æ£€æŸ¥ç¼“å­˜
    if (parseHeightToPixels.memo.hasOwnProperty(value)) {
      return parseHeightToPixels.memo[value];
    }

    // 3. å¤„ç† px å•ä½æˆ–çº¯æ•°å­—å­—ç¬¦ä¸²
    if (typeof value === 'string') {
      // 3.1 çº¯æ•°å­—å­—ç¬¦ä¸² (å¦‚ '500')
      if (/^\d+\.?\d*$/.test(value)) {
        const num = parseFloat(value);
        parseHeightToPixels.memo[value] = num;
        return num;
      }

      // 3.2 px å•ä½å­—ç¬¦ä¸² (å¦‚ '500px' æˆ– '300.5px')
      if (value.toLowerCase().endsWith('px')) {
        const num = parseFloat(value);
        parseHeightToPixels.memo[value] = num;
        return num;
      }
    }

    // 4. å…¶ä»–å•ä½éœ€è¦ DOM è®¡ç®—
    const tempDiv = document.createElement('div');
    tempDiv.style.position = 'absolute';
    tempDiv.style.visibility = 'hidden';
    tempDiv.style.height = value;
    tempDiv.style.left = '-9999px';

    document.body.appendChild(tempDiv);
    const heightInPixels = parseFloat(getComputedStyle(tempDiv).height);
    document.body.removeChild(tempDiv);

    // å­˜å…¥ç¼“å­˜
    parseHeightToPixels.memo[value] = heightInPixels;
    return heightInPixels;
  }

  let statusMsg;
  function showStatusMsg(params, append = false) {
    if (!statusMsg) statusMsg = document.querySelector('#status .status__msg');
    params = typeof params === 'string' ? params : JSON.stringify(params);
    let html = statusMsg.innerHTML;
    if (append) {
      html += params;
    } else {
      html = params;
    }
    html = html.trim();
    statusMsg.innerHTML = html;
  }
})();

/* ä¸­é”®ç‚¹å‡»å±•å¼€æ–‡æ¡£æ ‘ JSç‰‡æ®µ */
(async () => {
  let g_reset = setInterval(main, 2000);

  function main() {
    if (document.querySelector('.sy__file')) {
      clearInterval(g_reset);
    } else {
      return false;
    }
    document.querySelector('.sy__file').addEventListener('mousedown', event => {
      if (event.button !== 1) return;
      if (!event.target.classList.contains('b3-list-item__text'))
        let target = event.target.parentNode;
      let temp = event.target;
      for (let i = 0; i < 4 && temp; i++) {
        if (temp?.getAttribute("data-type") === "navigation-file"
          || temp?.getAttribute("data-type") === "navigation-root") {
          target = temp;
          break;
        }
        temp = temp?.parentNode;
      }
      if (target?.getAttribute("data-type") === "navigation-file"
        || target?.getAttribute("data-type") === "navigation-root") {
        const b3ListItemToggle = target.querySelector('.b3-list-item__toggle');
        const title = target.querySelector('.b3-list-item__text');
        if (b3ListItemToggle.classList.contains('fn__hidden')) return;
        event.preventDefault();
        b3ListItemToggle.click();
        if (event.ctrlKey) {
          title.click();
        }
      }
    }, true);
  }
})();

/* from wilsons
å½“å¼€å¯ç­›é€‰å, ä¸ºä¸ªäººä»£ç , å¦‚æœéœ€è¦å…¨éƒ¨ wilsons ä»£ç è¯·å…³æ³¨
https://ld246.com/article/1724305128590/comment/1724740441386#comments
*/

// see https://ld246.com/article/1723539516115
(() => {

  // ä½¿ç”¨å…¼å®¹æ¨¡å¼ï¼Œ
  // å¦‚æœå·¦å³ç®­å¤´æœ‰é—®é¢˜ï¼Œå¯ä»¥ä½¿ç”¨å…¼å®¹æ¨¡å¼ï¼Œå…¼å®¹æ¨¡å¼ç”¨ctrl/cmd + æ–¹å‘é”®ç§»åŠ¨
  // é»˜è®¤falseï¼Œæœªå¼€å¯ï¼Œè®¾ä¸ºtrueå¼€å¯
  const useCompatibilityMode = false;

  ////////////// ä»¥ä¸‹ä»£ç ä¸æ¶‰åŠé…ç½®é¡¹ï¼Œå¦‚æ— å¿…è¦å‹¿åŠ¨ //////////////////////////
  // åˆ¤æ–­æ˜¯å¦Asriä¸»é¢˜
  const theme = siyuan.config.appearance.mode === 0 ? siyuan.config.appearance.themeLight : siyuan.config.appearance.themeDark;
  if (theme !== 'Asri') return;

  // è®¾ç½®ä¸‹ä¸€ä¸ªå…ƒç´ çš„ç„¦ç‚¹
  function nextElementFocus(currentFocus, nextElement) {
    currentFocus.classList.remove("b3-list-item--focus");
    nextElement?.classList.add("b3-list-item--focus");
  }


  // ç­‰å¾…å…ƒç´ æ¸²æŸ“å®Œæˆåæ‰§è¡Œ
  function whenElementExist(selector) {
    return new Promise(resolve => {
      const checkForElement = () => {
        let element;
        if (typeof selector === 'function') {
          element = selector();
        } else {
          element = document.querySelector(selector);
        }
        if (element) {
          resolve(element);
        } else {
          requestAnimationFrame(checkForElement);
        }
      };
      checkForElement();
    });
  }

  function monitorHintMenu(layoutCenter) {
    // å®šä¹‰ä¸€ä¸ªå›è°ƒå‡½æ•°å¤„ç† DOM å˜åŒ–
    const observerCallback = (mutationsList) => {
      mutationsList.forEach((mutation) => {
        if (mutation.type === 'childList') {
          // å½“ layout__center å…ƒç´ æœ‰æ–°çš„å­å…ƒç´ è¢«æ·»åŠ æˆ–åˆ é™¤æ—¶è§¦å‘
          mutation.addedNodes.forEach((node) => {
            if (node.classList && node.classList.contains('hint--menu')) {
              // æ£€æŸ¥æ–°æ·»åŠ çš„èŠ‚ç‚¹æ˜¯å¦æ˜¯ .hint--menu
              checkFnNoneClass(node);
            }
          });
        } else if (mutation.type === 'attributes' && mutation.target.classList.contains('hint--menu')) {
          // å½“ .hint--menu å…ƒç´ çš„å±æ€§å‘ç”Ÿå˜åŒ–æ—¶è§¦å‘
          checkFnNoneClass(mutation.target);
        }
      });
    };

    // æ£€æŸ¥æ˜¯å¦æœ‰ .fn_none ç±»
    function checkFnNoneClass(node) {
      const hasFnNoneClass = node.classList.contains('fn__none');
      if (!hasFnNoneClass) {
        // æ˜¾ç¤ºmenu
        hintMenuShow = true;
        if (hintMenuTimer) clearTimeout(hintMenuTimer);
      } else {
        // éšè—menu
        if (hintMenuTimer) clearTimeout(hintMenuTimer);
        hintMenuTimer = setTimeout(() => {
          hintMenuShow = false;
        }, 100);
      }
    }

    // é…ç½® MutationObserver
    const config = {childList: true, subtree: true, attributes: true, attributeFilter: ['class']};

    // åˆ›å»ºä¸€ä¸ªæ–°çš„ MutationObserver å®ä¾‹
    const observer = new MutationObserver(observerCallback);

    // å¼€å§‹è§‚å¯Ÿ layout__center å…ƒç´ 
    observer.observe(layoutCenter, config);

    // è¿”å›ä¸€ä¸ªå‡½æ•°ä»¥åœæ­¢è§‚å¯Ÿ
    return () => {
      observer.disconnect();
    };
  }

  // region group mode
  // è·å–ä¸‹ä¸€ä¸ªåˆ†ç»„å…ƒç´ çš„ç„¦ç‚¹
  function focusNextGroupButton() {
    const menu = document.querySelector(".hint--menu:not(.fn__none)");
    const currentFocus = menu.querySelector('.b3-list-item--focus');
    let nextElement = currentFocus.nextElementSibling;

    // ç»§ç»­æŸ¥æ‰¾ä¸‹ä¸€ä¸ªå…ƒç´ ï¼Œç›´åˆ°æ‰¾åˆ°ä¸€ä¸ªæŒ‰é’®æˆ–.b3-menu__separator
    while (nextElement && nextElement.classList.contains('b3-list-item') && !nextElement.classList.contains('b3-menu__separator')) {
      nextElement = nextElement.nextElementSibling;
    }

    // å¦‚æœæ‰¾åˆ°äº†.b3-menu__separatorï¼Œå°±èšç„¦åˆ°å®ƒçš„å‰ä¸€ä¸ªæŒ‰é’®
    if (nextElement && nextElement.classList.contains('b3-menu__separator')) {
      nextElement = nextElement.nextElementSibling;
    }

    // å¦‚æœæ²¡æœ‰æ‰¾åˆ°ä»»ä½•æŒ‰é’®æˆ–.b3-menu__separatorï¼Œå¾ªç¯åˆ°åˆ—è¡¨å¼€å¤´
    if (!nextElement || !nextElement.classList.contains('b3-list-item')) {
      nextElement = menu.querySelector('.b3-list-item');
    }

    nextElementFocus(currentFocus, nextElement);
  }

  // è·å–ä¸Šä¸€ä¸ªåˆ†ç»„å…ƒç´ çš„ç„¦ç‚¹
  function focusPreviousGroupButton() {
    const menu = document.querySelector(".hint--menu:not(.fn__none)");
    const currentFocus = menu.querySelector('.b3-list-item--focus');
    let previousElement = currentFocus.previousElementSibling;

    // ç»§ç»­æŸ¥æ‰¾ä¸Šä¸€ä¸ªå…ƒç´ ï¼Œç›´åˆ°æ‰¾åˆ°ä¸€ä¸ªæŒ‰é’®æˆ–.b3-menu__separator
    while (previousElement && previousElement.classList.contains('b3-list-item') && !previousElement.classList.contains('b3-menu__separator')) {
      previousElement = previousElement.previousElementSibling;
    }

    // å¦‚æœæ‰¾åˆ°äº†.b3-menu__separatorï¼Œå°±èšç„¦åˆ°å®ƒçš„å‰ä¸€ä¸ªæŒ‰é’®
    if (previousElement && previousElement.classList.contains('b3-menu__separator')) {
      previousElement = previousElement.previousElementSibling;
    }

    // å¦‚æœæ²¡æœ‰æ‰¾åˆ°ä»»ä½•æŒ‰é’®æˆ–.b3-menu__separatorï¼Œå¾ªç¯åˆ°åˆ—è¡¨ç»“å°¾
    if (!previousElement || !previousElement.classList.contains('b3-list-item')) {
      previousElement = menu.querySelector('.b3-list-item:last-child');
    }

    nextElementFocus(currentFocus, getGroupFirstElement(previousElement));
  }

  // è·å–åˆ†ç»„çš„ç¬¬ä¸€ä¸ªå…ƒç´ 
  function getGroupFirstElement(currentFocus) {
    const menu = document.querySelector(".hint--menu:not(.fn__none)");
    let previousElement = currentFocus.previousElementSibling;

    // ç»§ç»­æŸ¥æ‰¾ä¸Šä¸€ä¸ªå…ƒç´ ï¼Œç›´åˆ°æ‰¾åˆ°ä¸€ä¸ªæŒ‰é’®æˆ–.b3-menu__separator
    while (previousElement && previousElement.classList.contains('b3-list-item') && !previousElement.classList.contains('b3-menu__separator')) {
      previousElement = previousElement.previousElementSibling;
    }

    // å¦‚æœæ‰¾åˆ°äº†.b3-menu__separatorï¼Œå°±èšç„¦åˆ°å®ƒçš„å‰ä¸€ä¸ªæŒ‰é’®
    if (previousElement && previousElement.classList.contains('b3-menu__separator')) {
      previousElement = previousElement.nextElementSibling;
    }

    // å¦‚æœæ²¡æœ‰æ‰¾åˆ°ä»»ä½•æŒ‰é’®æˆ–.b3-menu__separatorï¼Œå¾ªç¯åˆ°åˆ—è¡¨ç»“å°¾
    if (!previousElement || !previousElement.classList.contains('b3-list-item')) {
      previousElement = menu.querySelector('.b3-list-item');
    }
    return previousElement;
  }

  // endregion group mode

  // region search mode
  function GetColData() {
    const menu = document.querySelector(".hint--menu:not(.fn__none)");
    const currentFocus = menu.querySelector('.b3-list-item--focus');
    const parent = currentFocus.parentNode;
    const b3ItemStyle = window.getComputedStyle(document.querySelector(".hint--menu .b3-list-item"))
    const lineHeight = +b3ItemStyle.lineHeight.slice(0, -2)
    const marginBottom = +b3ItemStyle.marginBottom.slice(0, -2)
    // æ¯ä¸ªç»„æœ‰groupSizeä¸ªå…ƒç´ 
    const groupSize = Math.floor(menu.offsetHeight / (lineHeight + marginBottom))

    // è®¡ç®—å½“å‰èšç„¦å…ƒç´ åœ¨å…¶çˆ¶å…ƒç´ ä¸­çš„ç´¢å¼•

    const childrens = []
    for (let children of parent.children){
      if (children.classList && children.classList.contains("b3-menu__separator")){
      }else{
        childrens.push(children)
      }
    }
    const currentFocusIndex = Array.prototype.indexOf.call(childrens, currentFocus);
    const totalGroup = Math.ceil(childrens.length / groupSize);
    const curGroup = Math.ceil((currentFocusIndex + 1) / groupSize);
    let rowNum = (currentFocusIndex + 1) % groupSize;
    if (rowNum === 0) {
      rowNum = groupSize;
    }
    return {
      parent: parent, rowNum: rowNum, groupSize: groupSize, curGroup: curGroup, childrens:childrens,
      totalGroup: totalGroup, currentFocusIndex: currentFocusIndex, currentFocus: currentFocus
    }
  }

  function moveFocusByCol(isToRight) {
    const data = GetColData();
    let newFocusIndex;
    if (data.curGroup === 1 && !isToRight) {
      newFocusIndex = SkipMenuToEnd(data, data.childrens);
    } else if (data.curGroup === data.totalGroup && isToRight) {
      newFocusIndex = data.rowNum - 1;
    } else if (data.curGroup === data.totalGroup - 1 && isToRight) {
      newFocusIndex = SkipMenuToEnd(data, data.childrens);
    } else {
      newFocusIndex = data.currentFocusIndex + (isToRight ? data.groupSize : -data.groupSize);
    }
    nextElementFocus(data.currentFocus, data.childrens[newFocusIndex]);
  }

  function SkipMenuToEnd(data, childrens) {
    let lastGroupButtonNum = childrens.length % data.groupSize;
    if (lastGroupButtonNum === 0) {
      lastGroupButtonNum = data.groupSize;
    }
    if (data.rowNum <= lastGroupButtonNum) {
      return childrens.length + data.rowNum - lastGroupButtonNum - 1;
    } else {
      return childrens.length - 1;
    }
  }

  // endregion search mode

  // region key monitor
  function AddKeyEventListener() {
    document.addEventListener('keydown', function (event) {
      let menu;
      if (useCompatibilityMode) {
        if (!(event.ctrlKey || event.metaKey)) return
        menu = document.querySelector(".hint--menu:not(.fn__none)");
      } else {
        if (!hintMenuShow) return;
        menu = document.querySelector(".hint--menu");
        menu.classList.remove("fn__none");
      }
      if (menu) Do(menu, event,)
    });
  }

  function Do(menu, event) {
    const sepEl = document.querySelector("div.b3-menu__separator");
    if (event.key === 'ArrowLeft' || (event.shiftKey && event.key === "Tab"))
      sepEl ? focusPreviousGroupButton() : moveFocusByCol(false);
    else if (event.key === "ArrowRight" || event.key === "Tab")
      sepEl ? focusNextGroupButton() : moveFocusByCol(true);
    else if (event.key === 'Escape' || event.key === "Enter")
      menu.classList.add("fn__none");
    else return;
    event.stopPropagation();
    event.preventDefault();
  }

  // endregion key monitor

  // region Main
  let hintMenuShow = false;
  let hintMenuTimer = null;

  // ç›‘æ§menuæ˜¾ç¤ºçŠ¶æ€
  if (!useCompatibilityMode) {
    whenElementExist('.layout__center').then(async element => {
      monitorHintMenu(element); // ç­‰å¾…æ ‡ç­¾é¡µå®¹å™¨æ¸²æŸ“å®Œæˆåå¼€å§‹ç›‘å¬
    });
  }
  // ç›‘å¬æŒ‰é”®
  AddKeyEventListener()
  // endregion Main
})();
